int[] numbers = {1, 3, 5, 7, 9};
int max = Arrays.stream(numbers).max().getAsInt();
System.out.println("Max number is: " + max);

class Person {
  String name;
  int age;

  Person(String name, int age) {
    this.name = name;
    this.age = age;
  }

  void sayHello() {
    System.out.println("Hello, my name is " + name);
  }
}

@Test
public void testMaxNumber() {
int[] numbers = {1, 3, 5, 7, 9};

int max = Arrays.stream(numbers).max().getAsInt();
assertEquals(9, max);}
